from collections import defaultdict

def evaluate(response):
    def get_words(text):
        words = text.lower().split()
        processed = []
        for word in words:
            stripped = word.strip(string.punctuation)
            if len(stripped) >= 2:
                processed.append(stripped)
        return processed

    words = get_words(response)
    endings = defaultdict(int)
    for word in words:
        if len(word) < 2:
            continue
        ending = word[-2:]
        endings[ending] += 1

    total_pairs = 0
    for count in endings.values():
        if count >= 2:
            total_pairs += count * (count - 1) // 2

    return total_pairs >= 3

