def evaluate(response):
    expanded = re.sub(r"(\w+)'ll\b", r"\1 will", response, flags=re.IGNORECASE)
    present_aux = re.search(r'\b(am|is|are|has|have|do|does)\b', expanded, re.IGNORECASE)
    if present_aux:
        return False
    past_aux = re.search(r'\b(was|were|had|did)\b', expanded, re.IGNORECASE)
    if past_aux:
        return False
    past_ed = re.search(r'\b\w+ed\b', expanded)
    if past_ed:
        return False
    irregular_past = re.search(r'\b(went|ate|ran|saw|gave|took|made)\b', expanded, re.IGNORECASE)
    if irregular_past:
        return False
    third_person = re.search(r'\b(he|she|it)\s+\w+s\b', expanded, re.IGNORECASE)
    if third_person:
        return False
    if not re.search(r'\bwill\s+\w+', expanded, re.IGNORECASE):
        return False
    return True

